import React, { useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [text, setText] = useState(""); // State for input text
  const [translatedText, setTranslatedText] = useState(""); // State for translated text
  const [language, setLanguage] = useState("en"); // State for target language
  const [error, setError] = useState(""); // State for error messages

  const API_KEY = "91cf79f4052afe09bfadf2a63ea2fcb1bae79808"; // Replace with your API key

  const handleTranslate = async () => {
    if (!text.trim()) {
      setError("Please enter some text.");
      return;
    }

    try {
      const response = await axios.post(
        `https://translation.googleapis.com/language/translate/v2?key=${API_KEY}`,
        {
          q: text,
          target: language,
        }
      );
      setTranslatedText(response.data.data.translations[0].translatedText);
      setError("");
    } catch (err) {
      setError("Translation failed. Please try again.");
      console.error(err);
    }
  };

  const handleLanguageChange = (e) => {
    setLanguage(e.target.value);
  };

  return (
    <div className="App">
      <h1>Simple Translation App</h1>
      <div>
        <textarea
          placeholder="Enter text to translate"
          value={text}
          onChange={(e) => setText(e.target.value)}
          style={{ width: "300px", height: "100px" }}
        />
      </div>
      <div>
        <select value={language} onChange={handleLanguageChange}>
          <option value="en">English</option>
          <option value="he">Hebrew</option>
          <option value="ar">Arabic</option>
        </select>
        <button onClick={handleTranslate}>Translate</button>
      </div>
      {error && <p style={{ color: "red" }}>{error}</p>}
      <div>
        <h2>Translated Text:</h2>
        <p
          style={{
            direction: language === "he" || language === "ar" ? "rtl" : "ltr",
          }}
        >
          {translatedText}
        </p>
      </div>
    </div>
  );
}

export default App;
